function setentity(t_ent , t_value )
{
   msg = {}; msg.payload = {}; msg.payload.target = {}; msg.payload.data = {};
   msg.payload.target.entity_id=[t_ent];
   if (global.get("SS.mapping."+t_ent+".type") === "input_select") 
   { 
       msg.payload.data["option"] = global.get("SS_Export_Data." + t_value );
       msg.payload["service"] = "select_option";
       msg.payload["domain"] = "input_select";
       node.send([null , msg ]); 
   } else if (global.get("SS.mapping."+t_ent+".type") === "input_text")
   {
       msg.payload.data["value"] = global.get("SS_Export_Data." + t_value );
       msg.payload["service"] = "set_value";
       msg.payload["domain"] = "input_text";
       node.send([null, msg ]); 
   } else if (global.get("SS.mapping." + t_ent + ".type") === "input_boolean") {
       var tt_value = false;
       if (checktrue(global.get("SS_Export_Data." + t_value)) === true ) {tt_value = "turn_on";} else {tt_value = "turn_off";}
       msg.payload["service"] = tt_value ;
       msg.payload["domain"] = "input_boolean";
       node.send([null, msg ]);
   } else if (global.get("SS.mapping." + t_ent + ".type") === "input_boolean_txt") {
       if (checktrue(global.get("SS_Export_Data." + t_value)) === true) { tt_value = "turn_on"; } else { tt_value = "turn_off"; }
       msg.payload["service"] = tt_value;
       msg.payload["domain"] = "input_boolean";
       node.send([null, msg]);
   }  else
   { node.send([msg , null ]); }
}

